mapper.select:
select seq ,title ,artist ,runningTime ,bgmPrice ,contentPath from bgm where 1 = 1
and title like concat('%',#{content},'%') or artist like concat('%',#{content},'%')
and title = #{title} and artist = #{artist}

select userNickname 
from dotori 
where userNickname = #{userNickname};

mapper.update:
update dotori 
set 
currentDotori = currentDotori + #{dotoriCharge} 
where 
	userNickname = #{userNickname};

update dotori
set
currentDotori = #{amount}
where 
	userNickname = #{userNickname};

