mapper.insert:
insert into album(
	userNickname
	, title
	, content
	, imagePath
	, openScope
) values(
	#{userNickname}
	,#{title}
	,#{content}
	,#{imagePath}
	,#{openScope}
)

mapper.select:
Select seq , userNickname , title , content , imagePath , hits , create_date From album where del_yn != 'Y' and userNickname = #{userNickname}
and seq = #{seq}
order by seq desc
