mapper.insert:
insert into board(
	userNickname
	, title
	, content
) values(
	#{userNickname}
	, #{title}
	, #{content}
)

mapper.update:
update board 
set hits = hits+1
where seq = #{seq}
and userNickname = #{userNickname}

mapper.select:
SELECT seq , userNickname , title , content , imagePath , hits , create_date , update_date , del_yn , openScope
, CASE WHEN TIMESTAMPDIFF(HOUR, update_date, NOW()) <= 24 THEN 1 ELSE 0 END AS newcontent
FROM board WHERE del_yn = 'N' AND openScope = 1 AND userNickname = #{userNickname}
AND seq = #{seq}
ORDER BY seq DESC LIMIT #{limit} OFFSET #{offset}

select seq, userNickname, content,
DATE_FORMAT(update_date, '%Y.%m.%d %H:%i') as update_date_format 
from boardCMT 
where boardSeq=#{boardSeq}
and openScope=1
ORDER BY update_date_format DESC;
