mapper.update:
	UPDATE visit 
		SET content = #{newContent}, 
    	update_date = current_timestamp 
	WHERE userNickname = #{userNickname} 
  		AND targetNickname = #{targetNickname} 
  		AND content = #{originalContent}

mapper.delete:
delete from visit 
where userNickname = #{userNickname} 
and targetNickname = #{targetNickname} 
and content = #{content}
