mapper.select:
select seq ,title ,artist ,runningTime ,bgmPrice from bgm where 1 = 1
and title like concat('%',#{content},'%') or artist like concat('%',#{content},'%')
and title = #{title} and artist = #{artist}

select userNickname 
from dotori 
where userNickname = #{userNickname};

select currentDotori 
from dotori 
where userNickname = #{userNickname};
