mapper.insert:
insert into board(
	userNickname
	, title
	, content
) values(
	#{userNickname}
	, #{title}
	, #{content}
)

mapper.select:
SELECT seq , userNickname , title , content , imagePath , hits , create_date , update_date , del_yn , openScope From board where del_yn = 'N' and openScope = 1 and userNickname = #{userNickname}
and seq = #{seq}
order by seq desc
