mapper.select:
SELECT *, DATE_FORMAT(diary_date, '%Y-%m-%d') AS formatted_update_date FROM diary WHERE 1=1 AND DATE(diary_date) = CURDATE() -- 오늘 날짜에 해당하는 데이터만 선택
AND del_yn = 'n'
AND userNickname = #{userNickname}

mapper.update:
update
	diary
set
	title = #{title}
	,content = #{content}
	,update_date = current_timestamp
	,openScope = #{visibility}
where
	seq = #{seq}

mapper.insert:
insert into diaryCMT(
	diarySeq
	, userNickname
	, content
	, openScope
) values(
	#{diarySeq}
	,#{userNickname}
	,#{content}
	,#{openScope}
)

mapper.select:
		SELECT *, DATE_FORMAT(diary_date, '%Y-%m-%d') AS formatted_update_date
		FROM diary
		WHERE userNickname = #{userNickname}
		AND DATE(diary_date) = #{date}
		AND del_yn = 'n'
